import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './styles.module.css';

const NewItem = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    title: '',
    size: '',
    condition: '',
    images: []
  });
  const [preferences, setPreferences] = useState([]);
  const [newPreference, setNewPreference] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);
    if (files.length > 3) {
      alert('You can only upload up to 3 images');
      return;
    }
    setFormData(prev => ({
      ...prev,
      images: files
    }));
  };

  const handleAddPreference = () => {
    if (newPreference.trim() && !preferences.includes(newPreference)) {
      setPreferences(prev => [...prev, newPreference]);
      setNewPreference('');
    }
  };

  const handleRemovePreference = (index) => {
    setPreferences(prev => prev.filter((_, i) => i !== index));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here you would typically send the data to your backend
    console.log({ ...formData, preferences });
    navigate('/items');
  };

  return (
    <div className={styles.container}>
      <header className={styles.header}>
        <h1>Threads&Thrift</h1>
        <nav>
          <button onClick={() => navigate('/items')} className={styles.goBack}>
            Go Back
          </button>
        </nav>
      </header>

      <main className={styles.main}>
        <h2>New Item</h2>
        <form onSubmit={handleSubmit} className={styles.form}>
          <div className={styles.formGroup}>
            <label>Title</label>
            <input
              type="text"
              name="title"
              value={formData.title}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className={styles.formGroup}>
            <label>Size</label>
            <input
              type="text"
              name="size"
              value={formData.size}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className={styles.formGroup}>
            <label>Condition</label>
            <input
              type="text"
              name="condition"
              value={formData.condition}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className={styles.formGroup}>
            <label>Images (Max 3)</label>
            <input
              type="file"
              accept="image/*"
              multiple
              onChange={handleImageChange}
              className={styles.fileInput}
            />
          </div>

          <div className={styles.formGroup}>
            <label>Preferences</label>
            <div className={styles.preferenceInput}>
              <input
                type="text"
                value={newPreference}
                onChange={(e) => setNewPreference(e.target.value)}
                placeholder="Add preference"
              />
              <button
                type="button"
                onClick={handleAddPreference}
                className={styles.addButton}
              >
                Add
              </button>
            </div>
            <div className={styles.preferences}>
              {preferences.map((pref, index) => (
                <span key={index} className={styles.preference}>
                  {pref}
                  <button
                    type="button"
                    onClick={() => handleRemovePreference(index)}
                    className={styles.removePreference}
                  >
                    Ã—
                  </button>
                </span>
              ))}
            </div>
          </div>

          <button type="submit" className={styles.submitButton}>
            Post
          </button>
        </form>
      </main>
    </div>
  );
};

export default NewItem;
